package exerc1415;

public class Aula16 {

	public Aula16() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		int i = 1; // count ou cont
		int max = 10;
		
		System.out.println("Contando até " + max);
		
		while(i <= max) {
			System.out.println("Valor de i:" + i);
			i++; // i = i+1; ou i +=1;
		}
		
		System.out.println(i);
	}

}
